"use client"

import { useState, useCallback, useEffect } from "react"
import { useParams, useRouter } from "next/navigation"
import { PTELayoutClient } from "@/components/pte/pte-layout-client"
import { QuestionCard } from "@/components/speaking/question-card"
import { AudioRecorder } from "@/components/speaking/audio-recorder"
import { SpeakingResults } from "@/components/pte/speaking/SpeakingResults"
import { speakingTasks, speakingQuestions, communityRecordings } from "@/lib/mock-data"
import { useAppStore } from "@/lib/store/app-store"
import type { SpeakingScore } from "@/lib/types"
import { useToast } from "@/hooks/use-toast"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Card, CardContent } from "@/components/ui/card"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Button } from "@/components/ui/button"
import { MessageCircle, LayoutGrid, User, Play, Volume2, ArrowLeft } from "lucide-react"

export default function SpeakingTaskPage() {
  const params = useParams()
  const router = useRouter()
  const taskType = params.questionType as string
  const { toast } = useToast()
  const { user, checkRateLimit, incrementDailyUsage, initializeUser } = useAppStore()

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null)
  const [score, setScore] = useState<SpeakingScore | null>(null)
  const [isScoring, setIsScoring] = useState(false)
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState<string[]>([])

  // Initialize user on mount
  useEffect(() => {
    initializeUser()
  }, [initializeUser])

  const handleRecordingComplete = useCallback((blob: Blob) => {
    setAudioBlob(blob)
  }, [])

  // Find the task
  const task = speakingTasks.find((t) => t.type === taskType)

  // Filter questions for this task type
  const questions = speakingQuestions.filter((q) => q.taskType === taskType)
  const currentQuestion = questions[currentQuestionIndex] || questions[0]

  const handleSubmit = async () => {
    if (!audioBlob || !user) return

    // Check rate limit
    if (!checkRateLimit()) {
      toast({
        title: "Daily Limit Reached",
        description: "Upgrade your plan for more practice questions.",
        variant: "destructive",
      })
      return
    }

    setIsScoring(true)

    try {
      // Upload audio to storage
      const formData = new FormData()
      formData.append("file", audioBlob, `recording-${Date.now()}.webm`)
      formData.append("type", taskType)
      formData.append("questionId", currentQuestion.id)
      formData.append("ext", "webm")

      const uploadResponse = await fetch("/api/uploads/audio", {
        method: "POST",
        body: formData,
      })

      if (!uploadResponse.ok) {
        throw new Error("Failed to upload audio")
      }

      const { url } = await uploadResponse.json()

      // Submit to speaking attempts API
      const attemptResponse = await fetch("/api/speaking/attempts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          questionId: currentQuestion.id,
          type: taskType,
          audioUrl: url,
          durationMs: 30000, // TODO: Get actual duration from recorder
        }),
      })

      if (!attemptResponse.ok) {
        const errorData = await attemptResponse.json().catch(() => ({}))
        throw new Error(errorData.error || "Failed to submit attempt")
      }

      const { attempt, feedback } = await attemptResponse.json()

      // Map API response to UI Score format
      const realScore: SpeakingScore = {
        overall: attempt.overallScore || 0,
        content: attempt.contentScore || 0,
        pronunciation: attempt.pronunciationScore || 0,
        fluency: attempt.fluencyScore || 0,
        feedback: feedback || attempt.scores?.feedback || "No feedback available",
        wordAnalysis: [], // Word-level analysis not yet returned by backend
      }

      setScore(realScore)
      incrementDailyUsage()

      toast({
        title: "Scoring Complete",
        description: `Your score: ${realScore.overall}/90`,
      })
    } catch (error: any) {
      console.error("Submission error:", error)
      toast({
        title: "Error",
        description: error.message || "Failed to process your recording. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsScoring(false)
    }
  }

  const handlePrevious = () => {
    setCurrentQuestionIndex((prev) => Math.max(0, prev - 1))
    setScore(null)
    setAudioBlob(null)
  }

  const handleNext = () => {
    setCurrentQuestionIndex((prev) => Math.min(questions.length - 1, prev + 1))
    setScore(null)
    setAudioBlob(null)
  }

  const toggleBookmark = () => {
    setBookmarkedQuestions((prev) =>
      prev.includes(currentQuestion.id)
        ? prev.filter((id) => id !== currentQuestion.id)
        : [...prev, currentQuestion.id],
    )
  }

  if (!task || !currentQuestion) {
    return (
      <PTELayoutClient>
        <div className="flex items-center justify-center h-96">
          <div className="text-center space-y-4">
            <p className="text-muted-foreground">Task not found</p>
            <Button onClick={() => router.push('/practice/speaking')} variant="outline">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Speaking
            </Button>
          </div>
        </div>
      </PTELayoutClient>
    )
  }

  return (
    <PTELayoutClient>
      <div className="max-w-5xl mx-auto space-y-6">
        {/* Breadcrumb */}
        <nav className="text-sm text-muted-foreground flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => router.push('/practice/speaking')}
            className="p-0 h-auto font-normal"
          >
            <ArrowLeft className="h-4 w-4 mr-1" />
            Speaking
          </Button>
          <span>/</span>
          <span>{task.title}</span>
        </nav>

        {/* Question Card */}
        <QuestionCard
          task={task}
          question={currentQuestion}
          questionNumber={currentQuestionIndex + 1}
          totalQuestions={questions.length}
          onPrevious={handlePrevious}
          onNext={handleNext}
          onBookmark={toggleBookmark}
          isBookmarked={bookmarkedQuestions.includes(currentQuestion.id)}
        />

        {/* Audio Recorder */}
        <AudioRecorder
          prepTime={task.prepTime}

