import { useState, useCallback, useEffect } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { PracticeSessionLayout } from '@/components/pte/practice/PracticeSessionLayout'
import { useAppStore } from '@/lib/store/app-store'
import { useToast } from '@/hooks/use-toast'
import type { SpeakingScore } from '@/lib/types'

// Import specific components for different practice types
import { SpeakingQuestion } from '@/components/pte/practice/SpeakingQuestion'
import { WritingQuestion } from '@/components/pte/practice/WritingQuestion'
import { ReadingQuestion } from '@/components/pte/practice/ReadingQuestion'
import { ListeningQuestion } from '@/components/pte/practice/ListeningQuestion'

// Mock data imports
import { speakingTasks, speakingQuestions } from '@/lib/mock-data'

type PracticeType = 'speaking' | 'writing' | 'reading' | 'listening'

interface PracticeTaskPageProps {
  section: PracticeType
  questionType: string
}

export function PracticeTaskPage({ section, questionType }: PracticeTaskPageProps) {
  const params = useParams()
  const router = useRouter()
  const { toast } = useToast()
  const { user, checkRateLimit, incrementDailyUsage, initializeUser } = useAppStore()

  // For dynamic routes, we might also have an ID parameter
  const questionId = typeof params.id === 'string' ? params.id : undefined

  // State for practice session
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
  const [score, setScore] = useState<SpeakingScore | null>(null) // This will be more generic later
  const [isScoring, setIsScoring] = useState(false)

  // Initialize user on mount
  useEffect(() => {
    initializeUser()
  }, [initializeUser])

  // Get questions based on section and type
  const getQuestions = useCallback(() => {
    if (section === 'speaking') {
      return speakingQuestions.filter((q) => q.taskType === questionType)
    }
    // In a real implementation, we would fetch questions for other sections
    return []
  }, [section, questionType])

  const questions = getQuestions()
  const currentQuestion = questions[currentQuestionIndex] || questions[0]

  const handleSubmit = async (answer: any) => {
    if (!user) return

    // Check rate limit
    if (!checkRateLimit()) {
      toast({
        title: "Daily Limit Reached",
        description: "Upgrade your plan for more practice questions.",
        variant: "destructive",
      })
      return
    }

    setIsScoring(true)

    try {
      // Implementation will vary based on section type
      let response;
      if (section === 'speaking') {
        // Special handling for speaking (audio submission)
        // This will be implemented in the SpeakingQuestion component
      } else {
        // General submission for other types
        response = await fetch(`/api/${section}/attempts`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            questionId: currentQuestion?.id,
            type: questionType,
            answer: answer,
          }),
        })

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}))
          throw new Error(errorData.error || `Failed to submit ${section} attempt`)
        }

        const { attempt, feedback } = await response.json()

        // For now, using a generic response - this will be more specific per section
        const practiceScore: SpeakingScore = {
          overall: attempt.overallScore || Math.floor(Math.random() * 40) + 50, // Mock score
          content: attempt.contentScore || 0,
          pronunciation: attempt.pronunciationScore || 0,
          fluency: attempt.fluencyScore || 0,
          feedback: feedback || "Great work! Focus on improving your vocabulary range.",
          wordAnalysis: [],
        }

        setScore(practiceScore)
        incrementDailyUsage()
      }
    } catch (error: any) {
      console.error("Submission error:", error)
      toast({
        title: "Error",
        description: error.message || `Failed to process your ${section} response. Please try again.`,
        variant: "destructive",
      })
    } finally {
      setIsScoring(false)
    }
  }

  const handlePrevious = () => {
    setCurrentQuestionIndex((prev) => Math.max(0, prev - 1))
    setScore(null)
  }

  const handleNext = () => {
    setCurrentQuestionIndex((prev) => Math.min(questions.length - 1, prev + 1))
    setScore(null)
  }

  // Render the appropriate component based on section
  const renderQuestionComponent = () => {
    switch (section) {
      case 'speaking':
        return (
          <SpeakingQuestion
            question={currentQuestion}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={questions.length}
            onSubmit={handleSubmit}
            isScoring={isScoring}
            score={score}
            onPrevious={handlePrevious}
            onNext={handleNext}
            questionId={questionId}
          />
        )
      case 'writing':
        return (
          <WritingQuestion
            question={currentQuestion}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={questions.length}
            onSubmit={handleSubmit}
            isScoring={isScoring}
            score={score}
            onPrevious={handlePrevious}
            onNext={handleNext}
            questionId={questionId}
          />
        )
      case 'reading':
        return (
          <ReadingQuestion
            question={currentQuestion}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={questions.length}
            onSubmit={handleSubmit}
            isScoring={isScoring}
            score={score}
            onPrevious={handlePrevious}
            onNext={handleNext}
            questionId={questionId}
          />
        )
      case 'listening':
        return (
          <ListeningQuestion
            question={currentQuestion}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={questions.length}
            onSubmit={handleSubmit}
            isScoring={isScoring}
            score={score}
            onPrevious={handlePrevious}
            onNext={handleNext}
            questionId={questionId}
          />
        )
      default:
        return (
          <div className="text-center py-10">
            <p className="text-muted-foreground">Unsupported practice type: {section}</p>
          </div>
        )
    }
  }

  if (!currentQuestion) {
    return (
      <PracticeSessionLayout
        questionId={questionId || 'unknown'}
        questionType={questionType}
        section={section}
        title={`Practice - ${questionType}`}
      >
        <div className="flex items-center justify-center h-96">
          <div className="text-center space-y-4">
            <p className="text-muted-foreground">Question not found</p>
          </div>
        </div>
      </PracticeSessionLayout>
    )
  }

  return (
    <PracticeSessionLayout
      questionId={questionId || currentQuestion.id}
      questionType={questionType}
      section={section}
      title={`${questionType.replace(/_/g, ' ')} Question ${currentQuestionIndex + 1}`}
      difficulty={currentQuestion?.difficulty || 'Medium'}
    >
      {renderQuestionComponent()}
    </PracticeSessionLayout>
  )
}","file_path":"C:\Users\lemon\Documents\GitHub\pedagogsit-build-150\components\pte\practice\PracticeTaskPage.tsx"}

