interface SpeakingQuestionProps {
  question: any // Replace with proper speaking question type
  questionNumber: number
  totalQuestions: number
  onSubmit: (audioBlob: any) => void
  isScoring: boolean
  score: any // Replace with proper score type
  onPrevious: () => void
  onNext: () => void
  questionId?: string
}

export function SpeakingQuestion({
  question,
  questionNumber,
  totalQuestions,
  onSubmit,
  isScoring,
  score,
  onPrevious,
  onNext,
  questionId
}: SpeakingQuestionProps) {
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null)

  const handleRecordingComplete = useCallback((blob: Blob) => {
    setAudioBlob(blob)
  }, [])

  // Find the task based on the question type
  const task = speakingTasks.find(t => t.type === question?.taskType)

  return (
