import { config } from 'dotenv';
config({ path: '.env.local' });
config(); // fallback to .env
import { defineConfig } from 'drizzle-kit';

if (!process.env.DATABASE_URL) {
  throw new Error('DATABASE_URL is not set in the .env file');
}

export default defineConfig({
  schema: './lib/db/schema/index.ts',
  out: './drizzle',
  dialect: 'postgresql',
  dbCredentials: {
    url: process.env.DATABASE_URL,
  },
});

// Main schema index file - exports all individual schema files
export * from './users';
export * from './pte-questions';
export * from './mock-tests';
export * from './subscriptions';
export * from './ai-scoring';
export * from './speaking';
export * from './reading';
export * from './writing';
export * from './listening';
export * from './user-progress';
export * from './community';
export * from './activity';
export * from './conversation';
export * from './pte-attempts';

import { relations, sql } from 'drizzle-orm';
import {
  boolean,
  index,
  integer,
  jsonb,
  pgEnum,
  pgTable,
  text,
  timestamp,
  uuid,
} from 'drizzle-orm/pg-core';
import { userSubscriptions } from './subscriptions';
import { testAttempts, practiceSessions } from './pte-questions';

// Enums
export const difficultyEnum = pgEnum('difficulty_level', [
  'Easy',
  'Medium',
  'Hard',
]);

export const userRoleEnum = pgEnum('user_role', [
  'admin',
  'teacher',
  'user',
  'student', // Backward compatibility
]);

// Better Auth: User table
export const users = pgTable('users', {
  id: text('id').primaryKey(),
  name: text('name').notNull(),
  email: text('email').notNull().unique(),
  emailVerified: boolean('email_verified').default(false).notNull(),
  image: text('image'),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at')
    .defaultNow()
    .$onUpdate(() => new Date())
    .notNull(),
  // Custom fields for your app
  dailyAiCredits: integer('daily_ai_credits').notNull().default(10),
  aiCreditsUsed: integer('ai_credits_used').notNull().default(0),
  lastCreditReset: timestamp('last_credit_reset').defaultNow(),
  dailyPracticeLimit: integer('daily_practice_limit').notNull().default(3),
  practiceQuestionsUsed: integer('practice_questions_used').notNull().default(0),
  lastPracticeReset: timestamp('last_practice_reset').defaultNow(),
  role: userRoleEnum('role').default('user').notNull(),
});

// Better Auth: Session table
export const sessions = pgTable('sessions', {
  id: text('id').primaryKey(),
  expiresAt: timestamp('expires_at').notNull(),
  token: text('token').notNull().unique(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at')
    .$onUpdate(() => new Date())
    .notNull(),
  ipAddress: text('ip_address'),
  userAgent: text('user_agent'),
  userId: text('user_id')
    .notNull()
    .references(() => users.id, { onDelete: 'cascade' }),
});

// Better Auth: Account table
export const accounts = pgTable('accounts', {
  id: text('id').primaryKey(),
  accountId: text('account_id').notNull(),
  providerId: text('provider_id').notNull(),
  userId: text('user_id')
    .notNull()
    .references(() => users.id, { onDelete: 'cascade' }),
  accessToken: text('access_token'),
  refreshToken: text('refresh_token'),
  idToken: text('id_token'),
  accessTokenExpiresAt: timestamp('access_token_expires_at'),
  refreshTokenExpiresAt: timestamp('refresh_token_expires_at'),
  scope: text('scope'),
  password: text('password'),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at')
    .$onUpdate(() => new Date())
    .notNull(),
});

// Better Auth: Verification table
export const verifications = pgTable('verifications', {
  id: text('id').primaryKey(),
  identifier: text('identifier').notNull(),
  value: text('value').notNull(),
  expiresAt: timestamp('expires_at').notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at')
    .defaultNow()
    .$onUpdate(() => new Date())
    .notNull(),
});



// Relations
export const usersRelations = relations(users, ({ one, many }) => ({
  accounts: many(accounts),
  sessions: many(sessions),
  subscriptions: many(userSubscriptions),
  testAttempts: many(testAttempts),
  practiceSessions: many(practiceSessions),
}));

export const sessionsRelations = relations(sessions, ({ one }) => ({
  user: one(users, {
    fields: [sessions.userId],
    references: [users.id],
  }),
}));

export const accountsRelations = relations(accounts, ({ one }) => ({
  user: one(users, {
    fields: [accounts.userId],
    references: [users.id],
  }),
}));
//not need


export const verificationsRelations = relations(verifications, () => ({}));

// Type exports
export type User = typeof users.$inferSelect
export type NewUser = typeof users.$inferInsert
export type Session = typeof sessions.$inferSelect
export type NewSession = typeof sessions.$inferInsert
export type Account = typeof accounts.$inferSelect
export type NewAccount = typeof accounts.$inferInsert
export type Verification = typeof verifications.$inferSelect
export type NewVerification = typeof verifications.$inferInsert

// Note: teams is referenced in organizationsRelations but not defined yet
// It will be defined in teams.ts file

import { migrate } from 'drizzle-orm/postgres-js/migrator';
import { db, client } from '../lib/db/drizzle';

async function main() {
    console.log('⏳ Running migrations...');

    try {
        // This will run migrations from the migrations folder
        await migrate(db, { migrationsFolder: 'lib/db/migrations' });
        console.log('✅ Migrations completed successfully');
    } catch (error) {
        console.error('❌ Migration failed:', error);
        process.exit(1);
    } finally {
        await client.end();
    }
}

main();

